export function generateModalCss(themeColor: string, width: string, height: string) {
  const closeIconHeight = 36;
  const closeIconHeightOnePart = 36 / 3;
  const closeIconHeightTwoParts = (36 / 3) * 2;

  return `
    #transakRoot {
      z-index: 9997;
      position: fixed;
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
      background: rgba(0, 0, 0, 0.6);
    }

