      z-index: 9998;
      position: fixed;
      width: ${width};
      height: calc(${height} - ${closeIconHeightTwoParts}px);
      top: 50%;
      left: 50%;
      transform: translate(-50%, calc(-50% - ${closeIconHeightOnePart}px));
      margin-top: ${closeIconHeightTwoParts}px;
    }

    #transakCloseIcon {
      z-index: 9999;
      position: absolute;
      width: 36px;
      height: ${closeIconHeight}px;
      top: -${closeIconHeightTwoParts}px;
      right: 0;
      transition: 0.5s;
      color: #${themeColor};
      background: white;
      border-radius: 50%;
    }

    #transakCloseIcon:hover,
    #transakCloseIcon:focus {
      color: white;
      background: #${themeColor};
      cursor: pointer;
    }

    #transakIframe{
      width: 100%;
      height: 100%;
      border: none;
      border-radius: 10px;
      background: white;
    }

    @media screen and (max-width: 600px) {
      #transakModal {
        width: 100%;
        height: calc(100% - ${closeIconHeightTwoParts}px);
      }

      #transakIframe{
        border-radius: 10px 10px 0 0;
      }
    }
  `;
}
import { TransakConfig } from 'Types/sdk-config.types';
import { generateModalCss } from './generate-modal-css';

export function insertModalStyleToHtmlHead(config: TransakConfig) {
  const { themeColor = '1461db', widgetWidth = '480px', widgetHeight = '650px' } = config;
  const style = document.createElement('style');

  style.innerHTML = generateModalCss(themeColor, widgetWidth, widgetHeight);

  document.getElementsByTagName('head')[0]?.appendChild(style);

  return style;
}
import { closeIcon } from 'Assets/svg/close-icon';
import { generateGlobalTransakUrl } from 'Utils/generate-global-transak-url';
import { createIframe } from 'Utils/create-iframe';
import { TransakConfig } from 'Types/sdk-config.types';
import { insertModalStyleToHtmlHead } from './insert-modal-style-to-html-head';

export function renderIframeInModal(config: TransakConfig, closeRequest: () => void) {
  const styleElement = insertModalStyleToHtmlHead(config);
  const rootElement = document.createElement('div');
  const modal = document.createElement('div');
  const iframeElement = createIframe(generateGlobalTransakUrl(config));

  Object.assign(modal, {
    id: 'transakModal',
    innerHTML: closeIcon,
  });

  modal.appendChild(iframeElement);

  Object.assign(rootElement, {
    id: 'transakRoot',
    onclick: () => closeRequest(),
  });

  rootElement.appendChild(modal);

  document.getElementsByTagName('body')[0].appendChild(rootElement);

  document.getElementById('transakCloseIcon')?.addEventListener('click', () => closeRequest());

  return { styleElement, rootElement, iframeElement };
}
