import React, { useState, useEffect } from 'react';

const TransakWidget = () => {
  const [widgetUrl, setWidgetUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [accessToken, setAccessToken] = useState('');
  const [error, setError] = useState('');
  
  const [config, setConfig] = useState({
    fiatCurrency: 'USD',
    cryptoCurrencyCode: 'ETH',
    fiatAmount: 100,
    network: 'ethereum',
    walletAddress: '',
    email: '',
    countryCode: 'US',
    themeColor: '000000',
    isAutoFillUserData: true,
    hideMenu: false,
    exchangeScreenTitle: 'Buy Crypto',
    isFeeCalculationHidden: false,
    isDisableCrypto: false,
    disableWalletAddressForm: false
  });

  const [transactionType, setTransactionType] = useState('BUY');

  // Network and token configurations
  const networks = {
    'ethereum': { 
      name: 'Ethereum', 
      tokens: ['ETH', 'USDC', 'USDT', 'DAI', 'WBTC', 'UNI', 'LINK', 'AAVE'] 
    },
    'polygon': { 
      name: 'Polygon', 
      tokens: ['MATIC', 'USDC', 'USDT', 'DAI', 'WETH'] 
    },
    'bsc': { 
      name: 'BSC', 
      tokens: ['BNB', 'USDT', 'BUSD', 'CAKE', 'ADA'] 
    },
    'arbitrum': { 
      name: 'Arbitrum One', 
      tokens: ['ETH', 'USDC', 'USDT', 'ARB', 'GMX'] 
    },
    'optimism': { 
      name: 'Optimism', 
      tokens: ['ETH', 'USDC', 'USDT', 'OP'] 
    },
    'avalanche': { 
      name: 'Avalanche C-Chain', 
      tokens: ['AVAX', 'USDC', 'USDT', 'JOE'] 
    },
    'solana': { 
      name: 'Solana', 
      tokens: ['SOL', 'USDC', 'RAY', 'SRM'] 
    },
    'base': {
      name: 'Base',
      tokens: ['ETH', 'USDC', 'cbBTC']
    }
  };

  const fiatCurrencies = [
    'USD', 'EUR', 'GBP', 'CAD', 'AUD', 'JPY', 'INR', 
    'BRL', 'MXN', 'KRW', 'SGD', 'HKD', 'CHF', 'NOK', 'SEK'
  ];

  const countryCodes = [
    'US', 'GB', 'CA', 'AU', 'DE', 'FR', 'IT', 'ES', 
    'NL', 'IN', 'BR', 'MX', 'JP', 'KR', 'SG', 'HK'
  ];

  // Server-side functions (these would be implemented in your backend)
  const getAccessToken = async () => {
    try {
      setIsLoading(true);
      setError('');
      
      // This would be a call to your backend API
      const response = await fetch('/api/transak/auth', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          // Your backend will handle the API key and secret
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to get access token');
      }

      const data = await response.json();
      setAccessToken(data.access_token);
      return data.access_token;
    } catch (err) {
      setError('Failed to authenticate with Transak API');
      console.error('Auth error:', err);
      return null;
    } finally {
      setIsLoading(false);
    }
  };

  const createWidgetUrl = async (token) => {
    try {
      setIsLoading(true);
      setError('');

      const widgetConfig = {
        fiatCurrency: config.fiatCurrency,
        cryptoCurrencyCode: config.cryptoCurrencyCode,
        fiatAmount: config.fiatAmount,
        network: config.network,
        countryCode: config.countryCode,
        themeColor: config.themeColor,
        isAutoFillUserData: config.isAutoFillUserData,
        hideMenu: config.hideMenu,
        exchangeScreenTitle: transactionType === 'SELL' ? 'Sell Crypto' : 'Buy Crypto',
        isFeeCalculationHidden: config.isFeeCalculationHidden,
        isDisableCrypto: config.isDisableCrypto,
        disableWalletAddressForm: config.disableWalletAddressForm,
      };

      // Add optional fields
      if (config.walletAddress) {
        widgetConfig.walletAddress = config.walletAddress;
      }
      if (config.email) {
        widgetConfig.email = config.email;
      }

      // Add transaction type for sell
      if (transactionType === 'SELL') {
        widgetConfig.isSell = true;
      }

      // This would be a call to your backend API
      const response = await fetch('/api/transak/create-widget-url', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(widgetConfig),
      });

      if (!response.ok) {
        throw new Error('Failed to create widget URL');
      }

      const data = await response.json();
      setWidgetUrl(data.url);
    } catch (err) {
      setError('Failed to create widget URL');
      console.error('Widget URL creation error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const initializeWidget = async () => {
    const token = await getAccessToken();
    if (token) {
      await createWidgetUrl(token);
    }
  };

  useEffect(() => {
    // For demo purposes, we'll show a mock URL
    // In production, this would call initializeWidget()
    const demoUrl = `https://staging-global.transak.com?${new URLSearchParams({
      apiKey: 'demo-api-key',
      environment: 'STAGING',
      defaultFiatAmount: config.fiatAmount.toString(),
      defaultCryptoCurrency: config.cryptoCurrencyCode,
      defaultFiatCurrency: config.fiatCurrency,
      defaultNetwork: config.network,
      themeColor: config.themeColor,
      hideMenu: config.hideMenu.toString(),
      isSellOrBuy: transactionType,
    }).toString()}`;
    
    setWidgetUrl(demoUrl);
  }, [config, transactionType]);

  const handleConfigChange = (key, value) => {
    setConfig(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const handleNetworkChange = (network) => {
    setConfig(prev => ({
      ...prev,
      network: network,
      cryptoCurrencyCode: networks[network].tokens[0]
    }));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8">
      <div className="max-w-7xl mx-auto px-4">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-2">
            Transak Widget Integration
          </h1>
          <p className="text-gray-600 text-lg">
            Production-ready crypto trading with secure API integration
          </p>
        </div>

        {error && (
          <div className="mb-6 bg-red-50 border border-red-200 rounded-lg p-4">
            <div className="flex">
              <div className="text-red-800">
                <strong>Error:</strong> {error}
              </div>
            </div>
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Configuration Panel */}
          <div className="bg-white rounded-xl shadow-lg p-6">
            <h2 className="text-xl font-semibold mb-6 flex items-center">
              <span className="bg-blue-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-sm mr-2">
                ⚙
              </span>
              Widget Configuration
            </h2>
            
            {/* Transaction Type */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-3">
                Transaction Type
              </label>
              <div className="flex space-x-2">
                <button
                  onClick={() => setTransactionType('BUY')}
                  className={`flex-1 py-3 px-4 rounded-lg font-medium transition-all ${
                    transactionType === 'BUY' 
                      ? 'bg-green-500 text-white shadow-lg transform scale-105' 
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  🟢 BUY
                </button>
                <button
                  onClick={() => setTransactionType('SELL')}
                  className={`flex-1 py-3 px-4 rounded-lg font-medium transition-all ${
                    transactionType === 'SELL' 
                      ? 'bg-red-500 text-white shadow-lg transform scale-105' 
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  🔴 SELL
                </button>
              </div>
            </div>

            {/* Network Selection */}
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Blockchain Network
              </label>
              <select
                value={config.network}
                onChange={(e) => handleNetworkChange(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                {Object.entries(networks).map(([key, network]) => (
                  <option key={key} value={key}>
                    {network.name}
                  </option>
                ))}
              </select>
            </div>

            {/* Token Selection */}
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Cryptocurrency
              </label>
              <select
                value={config.cryptoCurrencyCode}
                onChange={(e) => handleConfigChange('cryptoCurrencyCode', e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                {networks[config.network].tokens.map(token => (
                  <option key={token} value={token}>
                    {token}
                  </option>
                ))}
              </select>
            </div>

            {/* Fiat Currency */}
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Fiat Currency
              </label>
              <select
                value={config.fiatCurrency}
                onChange={(e) => handleConfigChange('fiatCurrency', e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                {fiatCurrencies.map(currency => (
                  <option key={currency} value={currency}>
                    {currency}
                  </option>
                ))}
              </select>
            </div>

            {/* Country Code */}
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Country
              </label>
              <select
                value={config.countryCode}
                onChange={(e) => handleConfigChange('countryCode', e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                {countryCodes.map(code => (
                  <option key={code} value={code}>
                    {code}
                  </option>
                ))}
              </select>
            </div>

            {/* Amount */}
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Default Amount ({config.fiatCurrency})
              </label>
              <input
                type="number"
                value={config.fiatAmount}
                onChange={(e) => handleConfigChange('fiatAmount', parseInt(e.target.value) || 100)}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                min="10"
                max="10000"
              />
            </div>

            {/* Wallet Address */}
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Wallet Address (Optional)
              </label>
              <input
                type="text"
                value={config.walletAddress}
                onChange={(e) => handleConfigChange('walletAddress', e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="0x... or wallet address"
              />
            </div>

            {/* Email */}
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Email (Optional)
              </label>
              <input
                type="email"
                value={config.email}
                onChange={(e) => handleConfigChange('email', e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                placeholder="user@example.com"
              />
            </div>

            {/* Theme Color */}
            <div className="mb-6">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Theme Color
              </label>
              <div className="flex items-center space-x-3">
                <input
                  type="color"
                  value={`#${config.themeColor}`}
                  onChange={(e) => handleConfigChange('themeColor', e.target.value.slice(1))}
                  className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                />
                <input
                  type="text"
                  value={config.themeColor}
                  onChange={(e) => handleConfigChange('themeColor', e.target.value.replace('#', ''))}
                  className="flex-1 p-2 border border-gray-300 rounded-lg"
                  placeholder="000000"
                />
              </div>
            </div>

            {/* Options */}
            <div className="space-y-3">
              <label className="flex items-center">
                <input
                  type="checkbox"
                  checked={config.hideMenu}
                  onChange={(e) => handleConfigChange('hideMenu', e.target.checked)}
                  className="mr-2"
                />
                <span className="text-sm">Hide Menu</span>
              </label>
              
              <label className="flex items-center">
                <input
                  type="checkbox"
                  checked={config.isAutoFillUserData}
                  onChange={(e) => handleConfigChange('isAutoFillUserData', e.target.checked)}
                  className="mr-2"
                />
                <span className="text-sm">Auto-fill User Data</span>
              </label>

              <label className="flex items-center">
                <input
                  type="checkbox"
                  checked={config.disableWalletAddressForm}
                  onChange={(e) => handleConfigChange('disableWalletAddressForm', e.target.checked)}
                  className="mr-2"
                />
                <span className="text-sm">Disable Wallet Address Form</span>
              </label>
            </div>
          </div>

          {/* Widget Preview */}
          <div className="bg-white rounded-xl shadow-lg p-6">
            <h2 className="text-xl font-semibold mb-6 flex items-center">
              <span className="bg-green-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-sm mr-2">
                📱
              </span>
              Live Widget Preview
            </h2>
            
            {isLoading ? (
              <div className="flex flex-col items-center justify-center h-96 space-y-4">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
                <p className="text-gray-600">Loading Transak widget...</p>
              </div>
            ) : (
              <div className="border-2 border-gray-200 rounded-lg overflow-hidden shadow-inner">
                <iframe
                  src={widgetUrl}
                  width="100%"
                  height="650px"
                  frameBorder="0"
                  allow="camera;microphone;payment;encrypted-media"
                  title="Transak Widget"
                  className="w-full"
                />
              </div>
            )}

            <div className="mt-6 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-lg">
              <h3 className="text-sm font-semibold text-gray-800 mb-3">
                Current Configuration:
              </h3>
              <div className="grid grid-cols-2 gap-2 text-xs text-gray-600">
                <div><strong>Type:</strong> {transactionType}</div>
                <div><strong>Network:</strong> {networks[config.network].name}</div>
                <div><strong>Token:</strong> {config.cryptoCurrencyCode}</div>
                <div><strong>Fiat:</strong> {config.fiatCurrency}</div>
                <div><strong>Amount:</strong> {config.fiatAmount}</div>
                <div><strong>Country:</strong> {config.countryCode}</div>
              </div>
            </div>

            <button
              onClick={initializeWidget}
              disabled={isLoading}
              className="w-full mt-4 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-300 text-white font-medium py-3 px-4 rounded-lg transition-colors"
            >
              {isLoading ? 'Loading...' : 'Refresh Widget'}
            </button>
          </div>
        </div>

        {/* API Integration Guide */}
        <div className="mt-8 bg-white rounded-xl shadow-lg p-6">
          <h2 className="text-xl font-semibold mb-4">🔧 Production Implementation Guide</h2>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-gray-50 p-4 rounded-lg">
              <h3 className="font-semibold mb-3 text-green-600">✅ Backend Implementation (/api/transak/)</h3>
              <ul className="text-sm space-y-2 text-gray-700">
                <li>• <strong>auth</strong> - Get access token using API key + secret</li>
                <li>• <strong>create-widget-url</strong> - Create secure widget URL</li>
                <li>• <strong>refresh-token</strong> - Refresh expired tokens</li>
                <li>• Environment variables: API_KEY, API_SECRET</li>
                <li>• CORS configuration for your domain</li>
              </ul>
            </div>
            
            <div className="bg-gray-50 p-4 rounded-lg">
              <h3 className="font-semibold mb-3 text-blue-600">🔒 Security Features</h3>
              <ul className="text-sm space-y-2 text-gray-700">
                <li>• API secrets never exposed to client</li>
                <li>• Domain verification with reference domain</li>
                <li>• Secure token-based authentication</li>
                <li>• HTTPS-only widget URLs</li>
                <li>• Rate limiting and validation</li>
              </ul>
            </div>
          </div>

          <div className="mt-6 p-4 bg-yellow-50 rounded-lg border border-yellow-200">
            <h3 className="font-medium text-yellow-800 mb-2">⚠️ Demo Mode Active</h3>
            <p className="text-sm text-yellow-700">
              This demo uses a mock widget URL. In production, implement the backend APIs 
              to securely handle Transak authentication and widget URL creation.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TransakWidget;
